import React, { Component } from "react";
import Button from "@material-ui/core/Button";
import "./App.css";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import IconButton from "@material-ui/core/IconButton";
import Icon from "@material-ui/core/Icon";
import axios from "axios";
import ResizeImage from "react-resize-image";

class App extends Component {
  constructor() {
    super();
    this.state = {
      upload: false,
      connected: false,
      image: null,
      converted_image: null
    };
  }

  handleChange = event => {
    let reader = new FileReader();
    reader.onload = e => {
      this.compress(e);
      this.setState({ image: e.target.result });
    };
    if (event.target.files.length > 0)
      reader.readAsDataURL(event.target.files[0]);

    this.setState({ upload: true });
  };

  compress(pic) {
    const width = 125;
    const height = 125;
    const fileName = "scaledPic";

    reader.onload = event => {
      const img = pic;
      img.src = event.target.result;
      img.onload = () => {
        const elem = document.createElement("canvas");
        elem.width = width;
        elem.height = height;
        const ctx = elem.getContext("2d");
        // img.width and img.height will contain the original dimensions
        ctx.drawImage(img, 0, 0, width, height);
        ctx.canvas.toBlob(
          blob => {
            const file = new File([blob], fileName, {
              type: "image/png",
              lastModified: Date.now()
            });
            console.log("new file: ", file);
          },
          "image/png",
          1
        );
      };
      // (reader.onerror = error => console.log(error));
    };
  }

  getDataUrl = url => {
    const base = url.replace(/^data:image\/(png|jpg);base64,/, "");
    try {
      axios
        .post("http://localhost:3001/convert", {
          image64: base
        })
        .then(response => {
          console.log(response);
          this.setState({ converted_image: response });
        })
        .catch(error => {
          console.log(error);
        });
    } catch (error) {
      console.log(error);
    }
  };

  render() {
    const { upload, connected, image } = this.state;

    return (
      <div className="App">
        <AppBar position="static">
          <Toolbar>
            <Typography variant="h6" color="inherit" style={{ flexGrow: 1 }}>
              Printer UI
            </Typography>
            <IconButton
              style={{
                marginLeft: -12,
                marginRight: 20
              }}
              onClick={() =>
                this.setState(prev => ({ connected: !prev.connected }))
              }
              color="inherit"
              aria-label="Menu"
            >
              <Icon>{connected ? "wifi_off" : "wifi"}</Icon>
            </IconButton>
          </Toolbar>
        </AppBar>
        <header className="App-header">
          {upload ? (
            <div
              style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center"
              }}
            >
              <div
                style={{
                  backgroundColor: "white",
                  borderRadius: 4,
                  height: 125,
                  width: 125,
                  boxShadow: "0px 2px 5px 0px rgba(0,0,0,0.5)",
                  margin: 10,
                  backgroundImage: `url(${image})`,
                  backgroundSize: "125px 125px",
                  backgroundRepeat: "no-repeat",
                  backgroundPosition: "center"
                }}
              />
              {/* <Icon color="primary">arrow_forward_ios</Icon>
              <div
                style={{
                  // backgroundImage: `url(${this.state.converted_image})`,
                  backgroundColor: "white",
                  borderRadius: 4,
                  height: 125,
                  width: 125,
                  boxShadow: "0px 2px 5px 0px rgba(0,0,0,0.5)",
                  margin: 10
                }}
              >
                <img
                  src={this.state.converted_image}
                  alt="new pic"
                  style={{ width: "100%", height: "90%" }}
                />
              </div> */}
            </div>
          ) : null}
          <div
            style={{
              display: "flex",
              justifyContent: "space-around",
              alignItems: "center",
              width: "75%"
            }}
          >
            <input
              type="file"
              id="picker"
              style={{ display: "none  " }}
              onChange={this.handleChange}
            />
            <label
              htmlFor="picker"
              style={{
                border: "none",
                borderRadius: 4,
                backgroundColor: upload ? "red" : "blue",
                margin: 10,
                padding: 10,
                cursor: "pointer"
              }}
            >
              {upload ? "Re-Upload" : "Upload"}
              <Icon style={{ marginLeft: 10 }}> cloud_upload</Icon>
            </label>
            {/* <Button
              variant="contained"
              color={upload ? "secondary" : "primary"}
              style={{
                margin: 10
              }}
              //onClick={() => this.setState(prev => ({ upload: !prev.upload }))}
            >


            </Button> */}
            {/* {upload ? (
              <Button
                variant="contained"
                color="primary"
                style={{
                  margin: 10
                }}
                onClick={() => this.setState({ print: true })}
              >
                Print
                <Icon style={{ marginLeft: 10 }}> print</Icon>
              </Button>
            ) : null} */}
          </div>
        </header>
      </div>
    );
  }
}

export default App;
