import React, { Component } from "react";
import Button from "@material-ui/core/Button";
import "./App.css";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import Typography from "@material-ui/core/Typography";
import IconButton from "@material-ui/core/IconButton";
import Icon from "@material-ui/core/Icon";

class App extends Component {
  constructor() {
    super();
    this.state = {
      upload: false,
      connected: false,
      image: null,
      converted_image: null
    };
  }

  handleChange = event => {
    let reader = new FileReader();
    reader.onload = e => {
      this.getDataUrl(e.target.result);
      this.setState({ image: e.target.result });
    };
    if (event.target.files.length > 0)
      reader.readAsDataURL(event.target.files[0]);

    this.setState({ upload: true });
  };

  getDataUrl = async url => {
    // var image = new Image();

    // image.onload = function() {
    //   var canvas = document.createElement("canvas");
    //   // canvas.width = "100px"; // or 'width' if you want a special/scaled size
    //   // canvas.height = "100px"; // or 'height' if you want a special/scaled size

    //   canvas.getContext("2d").drawImage(this, 0, 0);

    //   // Get raw image data

    //   canvas
    //     .toDataURL("image/png")
    //     .replace(/^data:image\/(png|jpg);base64,/, "");
    // };

    // image.src = url;
    let backend_image = await fetch("http://localhost:3001/convert", {
      method: "POST",
      headers: { Allow: "application/json", "Contet-Type": "application/json" },
      body: JSON.stringify({
        image64: url,
        timesent: new Date()
      })
    });
    let response = await backend_image.json();
    console.log("RESPONSE: ", response);
    this.setState({ converted_image: response });
  };

  render() {
    const { upload, connected, image } = this.state;

    return (
      <div className="App">
        <AppBar position="static">
          <Toolbar>
            <Typography variant="h6" color="inherit" style={{ flexGrow: 1 }}>
              Printer UI
            </Typography>
            <IconButton
              style={{
                marginLeft: -12,
                marginRight: 20
              }}
              onClick={() =>
                this.setState(prev => ({ connected: !prev.connected }))
              }
              color="inherit"
              aria-label="Menu"
            >
              <Icon>{connected ? "wifi_off" : "wifi"}</Icon>
            </IconButton>
          </Toolbar>
        </AppBar>
        <header className="App-header">
          {upload ? (
            <div
              style={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center"
              }}
            >
              <div
                style={{
                  backgroundColor: "white",
                  borderRadius: 4,
                  height: 200,
                  width: 200,
                  boxShadow: "0px 2px 5px 0px rgba(0,0,0,0.5)",
                  margin: 10,
                  backgroundImage: `url(${image})`,
                  backgroundSize: "200px 200px",
                  backgroundRepeat: "no-repeat",
                  backgroundPosition: "center"
                }}
              >
                {/* <img
                  src={image}
                  alt="preview"
                  style={{ width: "100%", height: "100%" }}
                /> */}
              </div>
              <Icon color="primary">arrow_forward_ios</Icon>
              <div
                style={{
                  backgroundImage: this.state.converted_image,
                  backgroundColor: "white",
                  borderRadius: 4,
                  height: 200,
                  width: 200,
                  boxShadow: "0px 2px 5px 0px rgba(0,0,0,0.5)",
                  margin: 10
                }}
              >
                <img
                  src={null}
                  alt="new pic"
                  style={{ width: "100%", height: "90%" }}
                />
              </div>
            </div>
          ) : null}
          <div
            style={{
              display: "flex",
              justifyContent: "space-around",
              alignItems: "center",
              width: "75%"
            }}
          >
            <input
              type="file"
              id="picker"
              style={{ display: "none  " }}
              onChange={this.handleChange}
            />
            <label
              htmlFor="picker"
              style={{
                border: "none",
                borderRadius: 4,
                backgroundColor: upload ? "red" : "blue",
                margin: 10,
                padding: 10,
                cursor: "pointer"
              }}
            >
              {upload ? "Re-Upload" : "Upload"}
              <Icon style={{ marginLeft: 10 }}> cloud_upload</Icon>
            </label>
            {/* <Button
              variant="contained"
              color={upload ? "secondary" : "primary"}
              style={{
                margin: 10
              }}
              //onClick={() => this.setState(prev => ({ upload: !prev.upload }))}
            >


            </Button> */}
            {upload ? (
              <Button
                variant="contained"
                color="primary"
                style={{
                  margin: 10
                }}
                onClick={() => this.setState({ print: true })}
              >
                Print
                <Icon style={{ marginLeft: 10 }}> print</Icon>
              </Button>
            ) : null}
          </div>
        </header>
      </div>
    );
  }
}

export default App;
